// RegForm.js
import React, { useState, useEffect } from 'react';
import '../src/regForm.css';
import imageSrc from './pics1.png';
import Modal from './components/modal';


const labels = {
    'uppercase': 'At least 1 uppercase letter is required',
    'lowercase': 'At least 1 lowercase letter is required',
    'digit': 'At least 1 digit is required',
    'specialCharacter': 'At least 1 special character (!@#$%^&*()) is required',
    'length': 'password should be at least 8 characters long',
}

const RegForm = () => {
    const [selectedOptions, setSelectedOptions] = useState([]);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [passwordCriteriaFailed, setPasswordCriteriaFailed] = useState([]);
    const [emailMessage, setEmailMessage] = useState('');
    const [submissionMessage, setSubmissionMessage] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);


    useEffect(() => {
        const savedOptions = JSON.parse(localStorage.getItem('selectedOptions'));
        if (savedOptions) {
            setSelectedOptions(savedOptions);
        }
    }, []);


    const handleCheckboxChange = (option) => {
        let newOptions = []
        if (selectedOptions.find(item => item === option)) {
            newOptions = selectedOptions.filter(item => item !== option);
        }
        else {
            newOptions = [...selectedOptions, option];
        }
        setSelectedOptions(newOptions);

        localStorage.setItem('selectedOptions', JSON.stringify(newOptions));
    };

    const checkEmailValid = (value) => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(value);
    }


    const handleEmailChange = (e) => {
        const { value } = e.target;
        setEmail(value);
        const isValidEmail = checkEmailValid(value)
        setEmailMessage(isValidEmail ? '' : 'Please enter a valid email address');
    };

    const handlePasswordChange = (e) => {
        const { value } = e.target;
        setPassword(value);
        const criteriaFailed = validatePassword(value, selectedOptions)
        setPasswordCriteriaFailed(criteriaFailed || [])
    }

    const validatePassword = (password, options) => {
        if (!password) return false;
        const hasUpperCase = /[A-Z]/.test(password);
        const hasLowerCase = /[a-z]/.test(password);
        const hasDigit = /\d/.test(password);
        const hasSpecialCharacter = /[!@#$%^&*()]/.test(password);
        const isAtLeastEightCharacters = password.length >= 8;
        const isMoreThanTenCharacters = password.length > 10;

        let criteriaFailed = []

        for (const option of options) {
            console.log(option)
            if (option === 'uppercase' && !hasUpperCase) {
                criteriaFailed.push('uppercase')
            }
            if (option === 'lowercase' && !hasLowerCase) {
                criteriaFailed.push('lowercase')
            }
            if (option === 'digit' && !hasDigit) {
                criteriaFailed.push('digit')
            }
            if (option === 'specialCharacter' && !hasSpecialCharacter) {
                criteriaFailed.push('specialCharacter')
            }
            if (option === 'length' && !isAtLeastEightCharacters) {
                criteriaFailed.push('length')
            }
            if (option === 'length_more_than_10' && !isMoreThanTenCharacters) {
                criteriaFailed.push('length_more_than_10')
            }
        }

        return criteriaFailed
    };


    const calculatePasswordStrength = (password) => {
        let criteriaMet = validatePassword(password, ['uppercase', 'lowercase', 'digit', 'specialCharacter', 'length_more_than_10'])
        if (criteriaMet.length === 0) {
            return 'hard'
        }

        criteriaMet = validatePassword(password, ['uppercase', 'lowercase', 'specialCharacter'])
        if (criteriaMet.length === 0) {
            return 'medium'
        }

        return 'easy'
    }


    const handleSubmit = (e) => {
        e.preventDefault();
        if (email.trim() === '' || password === '') {
            setSubmissionMessage('Please fill in all fields.');
            return;
        }
        const passwordValid = passwordCriteriaFailed.length === 0
        if (!passwordValid) {
            setSubmissionMessage('Password does not meet criteria.');
            return;
        }
        const formData = {
            email, password: selectedOptions,
        };
        console.log('Form data:', formData);
        setSubmissionMessage('Submission successful!');
        setEmail('');
        setPassword('')
        setPasswordCriteriaFailed([]);
    };

    const hasSelectedPasswordOptions = selectedOptions.length > 0
    const passwordStrength = calculatePasswordStrength(password)

    const formDisabled = !email || !password || !checkEmailValid(email) || !hasSelectedPasswordOptions || passwordCriteriaFailed?.length > 0


    return (
        <div className='container'>

            <div className='sub_container_one'>

                <img src={imageSrc} alt="Description" />

            </div>

            <form className='sub_container_two' onSubmit={handleSubmit}>

                <div className='settings'>

                    <button type="button" className='settings_btn' onClick={() => setIsModalOpen(true)}>
                        <span>
                            <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="30" height="30" viewBox="0 0 50 50">
                                <path d="M47.16,21.221l-5.91-0.966c-0.346-1.186-0.819-2.326-1.411-3.405l3.45-4.917c0.279-0.397,0.231-0.938-0.112-1.282 l-3.889-3.887c-0.347-0.346-0.893-0.391-1.291-0.104l-4.843,3.481c-1.089-0.602-2.239-1.08-3.432-1.427l-1.031-5.886 C28.607,2.35,28.192,2,27.706,2h-5.5c-0.49,0-0.908,0.355-0.987,0.839l-0.956,5.854c-1.2,0.345-2.352,0.818-3.437,1.412l-4.83-3.45 c-0.399-0.285-0.942-0.239-1.289,0.106L6.82,10.648c-0.343,0.343-0.391,0.883-0.112,1.28l3.399,4.863 c-0.605,1.095-1.087,2.254-1.438,3.46l-5.831,0.971c-0.482,0.08-0.836,0.498-0.836,0.986v5.5c0,0.485,0.348,0.9,0.825,0.985 l5.831,1.034c0.349,1.203,0.831,2.362,1.438,3.46l-3.441,4.813c-0.284,0.397-0.239,0.942,0.106,1.289l3.888,3.891 c0.343,0.343,0.884,0.391,1.281,0.112l4.87-3.411c1.093,0.601,2.248,1.078,3.445,1.424l0.976,5.861C21.3,47.647,21.717,48,22.206,48 h5.5c0.485,0,0.9-0.348,0.984-0.825l1.045-5.89c1.199-0.353,2.348-0.833,3.43-1.435l4.905,3.441 c0.398,0.281,0.938,0.232,1.282-0.111l3.888-3.891c0.346-0.347,0.391-0.894,0.104-1.292l-3.498-4.857 c0.593-1.08,1.064-2.222,1.407-3.408l5.918-1.039c0.479-0.084,0.827-0.5,0.827-0.985v-5.5C47.999,21.718,47.644,21.3,47.16,21.221z M25,32c-3.866,0-7-3.134-7-7c0-3.866,3.134-7,7-7s7,3.134,7,7C32,28.866,28.866,32,25,32z"></path>
                            </svg>
                        </span>

                    </button>
                    {!hasSelectedPasswordOptions && <div className="password-message">Please select password criteria from settings</div>}
                </div>



                <div className='registration'>
                    <h1>Register</h1>
                    <div className='fillup'>
                        <input type="email" id="email" name="email" placeholder="Email" autoComplete="email" value={email} onChange={handleEmailChange} disabled={!hasSelectedPasswordOptions} />
                        <div className="email-message">{emailMessage}</div>
                        <input type="password" id="password" name="password" placeholder="Password" value={password} autoComplete="new-password" onChange={handlePasswordChange} disabled={!hasSelectedPasswordOptions} />
                        <ul className="email-message">
                            {passwordCriteriaFailed?.map(item => (
                                <li key={item}>
                                    {labels[item]}
                                </li>
                            ))}
                        </ul>
                        {!!password && <div>
                            Password Strength: <span className={`password-strength password-strength--${passwordStrength}`}>{passwordStrength}</span>
                        </div>}
                    </div>
                    <div className='done'>
                        <button disabled={formDisabled}>Register</button>
                    </div>
                </div>
                {submissionMessage && <div className="submission-message">{submissionMessage}</div>}
            </form>

            <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} selectedOptions={selectedOptions} handleCheckboxChange={handleCheckboxChange} />

        </div>
    );
};

export default RegForm;
